mapper.insert:
            INSERT INTO TB_Dept (
                deptName
				, memberCount
            ) VALUES (
                #{deptName},
                #{memberCount}
            )

mapper.update:
UPDATE TB_Dept 
SET deptName=#{deptName}
    , memberCount=#{memberCount}
WHERE deptId=#{deptId}

